#include <iostream>
#include <stdexcept>

long long factorial(int n) {
    if (n < 0) {
        throw std::invalid_argument("n must be non-negative");
    }
    return (n == 0 || n == 1) ? 1LL : n * factorial(n - 1);
}

int main() {
    for (int i = 0; i < 6; ++i) {
        std::cout << i << " " << factorial(i) << std::endl;
    }
    return 0;
}

